     // initiate layout and plugins        App.init();        FormSamples.init();    });    var app_http_url = $("#app_http_url").val();    $(document).ready(function () {        if ($("#save_error").val()) { //保存失败提示            alert("保存失败!");        }        //大洲、国家、城市        var continent_country_city = {            change_continent: function () {                $("#continent option").click(function () {                    var continent_id = $.trim($(this).val());                    if ((continent_id !== 0) && (continent_id !== undefined) && (continent_id !== "")) {                        $.ajax({                            type: "get",                            url: app_http_url+"Iattraction/AjaxGetCountryByContinentId",                            dataType: "json",                            data: {continent_id: continent_id},                            success: function (data) {                                var country_opt_html = '<option value="0">-请选择国家-</option>';                                for (key in data) {                                    country_opt_html += '<option value="' + data[key]['ID'] + '">' + data[key]['NameCn'] + '</option>';                                }                                $("#country").html(country_opt_html);                                continent_country_city.change_country();                                $("#city").html('<option value="0">-请选择城市-</option>');                            }                        });                    }                });            },            change_country: function () {                $("#country option").click(function () {                    var country_id = $.trim($(this).val());                    if ((country_id !== 0) && (country_id !== undefined) && (country_id !== "")) {                        $.ajax({                            type: "get",                            url: app_http_url+"Iattraction/AjaxGetCityByCountryId",                            dataType: "json",                            data: {country_id: country_id},                            success: function (data) {                                var city_opt_html = '<option value="0">-请选择城市-</option>';                                for (k in data) {                                    city_opt_html += '<option value="' + data[k]['ID'] + '">' + data[k]['NameCn'] + '</option>';                                }                                $("#city").html(city_opt_html);                            }                        });                    }                });            }        };        continent_country_city.change_continent();        continent_country_city.change_country();    });    //form表单验证    function chkinput(form) {        if ($.trim(form.NameCn.value) == "") {            alert("请输入景点中文名称!");            form.NameCn.select();            return(false);        }        return true;    }</script><!-- END JAVASCRIPTS --></body><!-- END BODY --></html><?php }} ?>
